const fs = require('fs/promises');
const path = require('path');
const root = '/data/orchestration';
const queuePath = path.join(root, 'queue.json');
const statePath = path.join(root, 'state.json');

async function exists(p) {
  try { await fs.access(p); return true; } catch { return false; }
}

const results = [];
for (const p of [root, queuePath, statePath]) {
  results.push({ path: p, exists: await exists(p) });
}

let writable = true;
try {
  const testFile = path.join(root, 'tmp', `probe-${Date.now()}.txt`);
  await fs.mkdir(path.dirname(testFile), { recursive: true });
  await fs.writeFile(testFile, 'ok');
  await fs.unlink(testFile);
} catch (e) {
  writable = false;
  results.push({ path: root, writable: false, error: e.message });
}

return [{ json: { status: writable && results.every(r => r.exists || r.path === root) ? 'ok' : 'degraded', results } }];
