const services = {
  ollama: process.env.LLM_URL || 'http://192.168.10.133:11434',
  image: process.env.IMAGE_URL || 'http://192.168.10.133:8011',
  audio: process.env.AUDIO_URL || 'http://192.168.10.133:8012',
  video: process.env.VIDEO_URL || 'http://192.168.10.133:8013',
  chroma: process.env.CHROMA_URL || 'http://192.168.10.133:8018',
};

async function ping(name, url, path = '/health') {
  const target = url.endsWith('/') ? url.slice(0, -1) : url;
  const res = await fetch(`${target}${path}`, { method: 'GET' }).catch(err => ({ ok: false, statusText: err.message }));
  if (!res || !res.ok) {
    return { name, url, status: 'error', detail: res?.statusText || 'unreachable' };
  }
  const body = await res.text();
  return { name, url, status: 'ok', body };
}

const results = [];
results.push(await ping('ollama', services.ollama, '/api/tags'));
results.push(await ping('image', services.image, '/health'));
results.push(await ping('audio', services.audio, '/health'));
results.push(await ping('video', services.video, '/health'));
results.push(await ping('chroma', services.chroma, '/api/v2/heartbeat'));

return [{ json: { status: results.every(r => r.status === 'ok') ? 'ok' : 'degraded', results } }];
