const fs = require('fs/promises');
const path = require('path');
const root = '/data/orchestration';
const queuePath = path.join(root, 'queue.json');
const statePath = path.join(root, 'state.json');

const queue = JSON.parse(await fs.readFile(queuePath, 'utf8'));
const state = JSON.parse(await fs.readFile(statePath, 'utf8'));

const pendingMissing = [];
for (const p of queue.pending || []) {
  const jobPath = path.join(root, p.payload_path);
  try { await fs.access(jobPath); } catch { pendingMissing.push(p.id); }
}

const orphaned = [];
for (const jobId of Object.keys(state.processing || {})) {
  const jobPath = path.join(root, 'jobs', jobId, 'job.json');
  try { await fs.access(jobPath); } catch { orphaned.push(jobId); }
}

const ok = !pendingMissing.length && !orphaned.length;
return [{ json: { status: ok ? 'ok' : 'degraded', pending: (queue.pending || []).length, processing: Object.keys(state.processing || {}).length, pendingMissing, orphaned } }];
